sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here

	var product = new GlideRecord('x_snc_order_tracki_add_product');
	var order = new GlideRecord('x_snc_order_tracki_add_orders');
	var MaxId = new GlideAggregate('x_snc_order_tracki_add_orders');

	product.addQuery('name', current.product_name.getDisplayValue());

	product.query();


	if (product.getRowCount() == 0)
		{ 
			gs.addInfoMessage('No such product exists');
			current.setAbortAction(true);

		}
	else
		{

	        order.orderBy('ID');
	        order.query();
			MaxId.addAggregate('MAX', 'id');
			MaxId.query();

	        if (MaxId.next())
	         	current.id.setValue(1);
	        else 
	            current.id.setValue(MaxId.getAggregate('MAX') + 1);
			current.return_status.setValue('No return');
			current.total_price.setValue(product.getElement(price) * current.quantity.getSessionValue());
		}


})(current, previous);
